/* jQuery 1.5.1 - Minimal placeholder for compatibility */
window.$ = window.jQuery = function(selector) {
    if (typeof selector === 'string') {
        return {
            ready: function(fn) { 
                if (document.readyState !== 'loading') fn();
                else document.addEventListener('DOMContentLoaded', fn);
            },
            css: function(prop, val) {
                var els = document.querySelectorAll(selector);
                for (var i = 0; i < els.length; i++) {
                    if (val !== undefined) els[i].style[prop] = val;
                }
                return this;
            },
            attr: function(attr, val) {
                var els = document.querySelectorAll(selector);
                for (var i = 0; i < els.length; i++) {
                    if (val !== undefined) els[i].setAttribute(attr, val);
                }
                return this;
            }
        };
    } else if (typeof selector === 'function') {
        if (document.readyState !== 'loading') selector();
        else document.addEventListener('DOMContentLoaded', selector);
    }
    return {};
};

// Alias for compatibility
window.$$ = window.$;